@login_required(login_url='login')
def chat(request, username):
    another_user = User.objects.get(username=username)
    thread_object = Thread.objects.get_or_new(request.user, another_user)[0]
    chat_messages = ChatMessage.objects.filter(thread=thread_object)
    chat_with = f'chat with {username}'

    char = 'ğŸŒ¾'
    print(unicodedata.name(char))

    # make_emojis()

    context = {
        'other_user': another_user,
        'chat_messages': chat_messages,
        'page': chat_with
    }
    return render(request, 'inbox/chat.html', context)


@api_view(['GET', 'POST'])
def me_unicodes(request, cat, by, *args, **kwargs):
    order_by = ['id', '-id', 'name', '-name', 'emoji', '-emoji']
    if by in order_by:
        if cat == 'all':
            emojis = Emoji.objects.all().order_by(by)
        else:
            emojis = Emoji.objects.filter(name__contains=cat).order_by(by)

        serializer = EmojiSerializer(emojis, many=True)

        return Response(serializer.data)
    else:
        data = {
            'An error occured': 'In the order by parameter',
            'Error': 'ORDER BY PARAMETER DOES NOT EXIST',
            'Order by parameter': by,
            'Possible values are': order_by
        }
        return Response(data)


